import React, { useState, useEffect } from 'react';

function LoginForm() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // const onSubmit = () => {
    //     console.log('clicked')
    //     fetch('/login', {
    //         method: 'POST',
    //         headers: {
    //             'Content-Type': 'application/json',
    //         },
    //         body: JSON.stringify(formData),
    //     })
    //         .then((resp) => {
    //             if (resp.ok) {
    //                 return resp.json();
    //             } else {
    //                 alert('Invalid Login Credentials');
    //             }
    //         })
    //         .then((user) => onLogin(user))
    //         .catch((error) => {
    //             alert(error.message);
    //         })
    //     console.log('Login form submitted:',);
    // };

    // const onLogin = (user) => {
    //     // Handle the login logic when the response is successful
    //     console.log('User logged in:', user);
    // };

    const handleLogin = async () => {
        try{
            const response = await fetch('/users') // Fetch customer data
            const userData = await response.json()
            const user = userData.find((user) => user.username === formData.username);

            console.log(user)
            if (user && user._password_hash === formData.password) {
                console.log('Login successful')
                console.log(user.id)
            } else {
                console.log('Invalid Login')
            }

        } catch (error) {
            console.error('Error fetching customer data', error);
        };
    };
    console.log(formData)



    return (
        <div>
            <div>
                <label>Username:</label>
                <input
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                />
            </div>
            <div>
                <label htmlFor="password">Password:</label>
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                />
            </div>
            <button onClick={handleLogin}>Login</button>
        </div>
    );
};

export default LoginForm
