import React, { useState } from 'react';
import Modal from 'react-modal';

const ImageChanger = ({ onPatchRequest }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const handlePatchRequest = (newImage) => {
    // Perform your PATCH request with the new image
    // Call the onPatchRequest callback with the updated image data
    onPatchRequest(newImage);

    // Close the modal after making the PATCH request
    setModalIsOpen(false);
  };

  return (
    <div>
      <button onClick={() => setModalIsOpen(true)}>Update Image</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Image Updater"
      >
        {/* Add your image selection UI here */}
        <input type="file" onChange={(e) => handlePatchRequest(e.target.files[0])} />
        <button onClick={() => setModalIsOpen(false)}>Close</button>
      </Modal>
    </div>
  );
};

export default ImageChanger;
