import React, { useState } from "react";

const Calendar = ({appointment}) => {
    const [date, setDate] = useState(new Date());

    const daysInMonth = (month, year) => {
        return new Date(year, month + 1, 0).getDate();
    };

    const startOfMonth = () => {
        return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
    };

    const generateCalendar = () => {
        const totalDays = daysInMonth(date.getMonth(), date.getFullYear());
        const startDay = startOfMonth();

        const calendar = [];
        let day = 1;

        // Add blank cells for the days before the first day of the month
        for (let i = 0; i < startDay; i++) {
            calendar.push(<div key={`empty-${i}`} className="empty-cell"></div>);
        }

        // Add cells for each day in the month
        for (let i = 1; i <= totalDays; i++) {
            const currentDate = new Date(date.getFullYear(), date.getMonth(), i);
            const isAppointmentDay = appointments.some(appointment => {
                const appointmentDate = new Date(appointment.date);
                return (
                    appointmentDate.getFullYear() === currentDate.getFullYear() &&
                    appointmentDate.getMonth() === currentDate.getMonth() &&
                    appointmentDate.getDate() === currentDate.getDate()
                );
            });

            if (isAppointmentDay) {
                // Render appointment details
                const appointmentDetails = appointments
                    .filter(appointment => {
                        const appointmentDate = new Date(appointment.date);
                        return (
                            appointmentDate.getFullYear() === currentDate.getFullYear() &&
                            appointmentDate.getMonth() === currentDate.getMonth() &&
                            appointmentDate.getDate() === currentDate.getDate()
                        );
                    })
                    .map(appointment => (
                        <div key={appointment.id} className="appointment-cell">
                            {/* Display appointment details */}
                            {appointment.time} - {appointment.client_id}
                        </div>
                    ));

                calendar.push(
                    <div key={i} className="calendar-cell has-appointment">
                        {i}
                        <div className="appointment-details">{appointmentDetails}</div>
                    </div>
                );
            } else {
                // Render regular day
                calendar.push(
                    <div key={i} className="calendar-cell">
                        {i}
                    </div>
                );
            }

            day++;
        }

        return calendar;
    };

    const goToPrevMonth = () => {
        setDate(new Date(date.getFullYear(), date.getMonth() - 1, date.getDate()));
    };

    const goToNextMonth = () => {
        setDate(new Date(date.getFullYear(), date.getMonth() + 1, date.getDate()));
    };

    return (
        <div className="calendar-container">
            <div className="calendar-header">
                <button onClick={goToPrevMonth}>&lt;</button>
                <h2>{new Date(date).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</h2>
                <button onClick={goToNextMonth}>&gt;</button>
            </div>
            <div className="day-labels">
                <div>Sun</div>
                <div>Mon</div>
                <div>Tue</div>
                <div>Wed</div>
                <div>Thu</div>
                <div>Fri</div>
                <div>Sat</div>
            </div>
            <div className="calendar-grid">{generateCalendar()}</div>
        </div>
    );
};

export default Calendar;
